			function convertUTCToLocal(utcString) {
				if (!utcString) return '';
				const utcDate = new Date(utcString + 'Z'); // ç¢ºä¿å®ƒæ˜¯ UTC
				return utcDate.toLocaleString('zh-TW', {
					timeZone: 'Asia/Taipei',
					year: 'numeric',
					month: '2-digit',
					day: '2-digit',
					hour: '2-digit',
					minute: '2-digit',
					hour12: false
				});
			}
