  // åŠ è½½ Google Maps API çš„å‡½æ•°
  function loadGoogleMapsAPI(callback) {
	  if (window.google)
	  {
			if(window.google.maps) {
			  callback();
			  return;
		  }
	  }
	  var script = document.createElement("script");
	  script.src = "https://maps.googleapis.com/maps/api/js?key=AIzaSyAaRtvYPCxY0Ewi6oCyg16AdSlr74rx0q4";
	  script.async = true;
	  script.defer = true;
	  script.onload = callback;
	  document.head.appendChild(script);
  }
  
  // åˆå§‹åŒ–åœ°åœ–ï¼Œä¸¦åœ¨æŒ‡å®šä½ç½®æ·»åŠ æ¨™è¨˜
  function initMap(latitude, longitude) {
	  var mapOptions = {
		  center: { lat: latitude, lng: longitude },
		  zoom: 15
	  };
	  var map = new google.maps.Map(document.getElementById("map"), mapOptions);
	  new google.maps.Marker({
		  position: { lat: latitude, lng: longitude },
		  map: map,
		  title: "æ‚¨çš„ä½ç½®"
	  });
  }
  
  // æ›´æ–°å®šä½è³‡è¨ŠåŠåœ°åœ–
  function updateLocation() {
	  if (navigator.geolocation) {
		  navigator.geolocation.getCurrentPosition(function(position) {
			  var lat = position.coords.latitude;
			  var lng = position.coords.longitude;
			  document.getElementById("current-location").innerText = "å®šä½æˆåŠŸ";
			  loadGoogleMapsAPI(function() {
				  initMap(lat, lng);
			  });
		  }, function(error) {
			  document.getElementById("current-location").innerText = "ç„¡æ³•ç²å–ä½ç½®";
		  });
	  } else {
		  document.getElementById("current-location").innerText = "æ‚¨çš„ç€è¦½å™¨ä¸æ”¯æŒå®šä½";
	  }
  }
  
  // å¯¦æ™‚æ›´æ–°æ‰“å¡æ™‚é–“
  function updateTime() {
	  var now = new Date();
	  document.getElementById("current-time").innerText = "æ‰“å¡æ™‚é–“ï¼š" + now.toLocaleTimeString();
  }
  function sendCheckInData(lat, lng, lineId,type) {
	  fetch('/incheck', {
		  method: 'POST',
		  headers: {
			  'Content-Type': 'application/json',
		  },
		  body: JSON.stringify({latitude: lat, longitude: lng, line_id: lineId,type:type})
	  })
	  .then(response => response.json())
	  .then(data => {
		  alert(data.data);
	  })
	  .catch((error) => {
		  console.error('Error:', error);
	  });
  }
document.addEventListener("DOMContentLoaded", function() {
	//const liffId = "2006957760-wVrpD4em";
	
	const queryString = window.location.search;
														//alert("Query String: " + queryString);
	const decodedQueryString = decodeURIComponent(queryString);
														//alert("Decoded Query String: " + decodedQueryString);
	
	let daka_type = "in";  
	if (decodedQueryString.indexOf("daka_type=out") >= 0) {
		daka_type = "out";
	}
	
	const regex = /liffid=([a-zA-Z0-9\-]+)/; // Matches 'liffid=' followed by any alphanumeric or dash
	const match = decodedQueryString.match(regex);
	const liffId = match[1];
	//alert(liffId)
													//const liffId = "2006957760-wVrpD4em";
	liff.init({
			liffId: liffId
		}).then(() => {
			//alert("æº–å‚™å¥½äº†")
			updateTime();
			setInterval(updateTime, 1000);
			updateLocation();
			
			document.getElementById("checkin-btn").addEventListener("click", function() {
			//alert("æ‰“å¡æŒ‰é’®è¢«ç‚¹å‡»");  æ£€æŸ¥æŒ‰é’®ç‚¹å‡»äº‹ä»¶æ˜¯å¦è§¦å‘
			liff.getProfile()
			.then(function(profile) {
			  const lineId = profile.userId;
			  navigator.geolocation.getCurrentPosition(function(position) {
				  sendCheckInData(position.coords.latitude, position.coords.longitude, lineId,daka_type);
			  });
			});
			
		  });
			
		});
	  
	  
	  // æ‰“å¡æŒ‰éˆ•äº‹ä»¶è™•ç†
	  
  });

  
  
